import './App.css';
import React, { Component, useState, useEffect } from 'react'
import axios from 'axios'

const COLS = 50;
const ROWS = 50;

function to_2d_grid(preset){
    const rows = [];
    let offset = 0;
    for(let i =0; i < ROWS; i++){
	rows.push(preset.slice(offset, offset + COLS))
	offset += COLS;
    }

    return rows;
}

function App() {
    const [grid, setGrid] = useState([]);
    useEffect(() => {
	axios
	    .get('http://127.0.0.1:8000/api/worlds/1/')
	    .then(({data}) => {
		setGrid(to_2d_grid(Array.from(data.preset.slice(1,-1).split(','))))
	    })
    })
    console.log(grid)
    return (
	<div>
	    {grid.map((rows, i) =>
		rows.map((col, k) =>
		    <div
			key={`${i}-${k}`}
			style={{
			    width: 20,
			    height: 20,
			    backgroundColor: grdi[i][k] == " 1" ? "green" : "white",
			    border: 'solid 1px black'
			}}
		    />
		))}
	</div>
    )
}
